package casting;

public class ExplicitCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		short s=58;
		byte b=(byte)s;
		System.out.println(b);
		
		short ss=300;
		byte bb=(byte)ss;
		System.out.println(bb);
		
		float f=96.56f;
		int i=(int)f;
		System.out.println(i);
		
		double d=859.399d;
		float ff=(float)d;
		System.out.println(ff);
		
		
		
		
		
		
	}

}
