package casting;

public class ImplicitCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=20;
		long l=a;
		System.out.println(a);
		
		double d=a;
		System.out.println(a);
		
		float f=20.36f;
		double dd=f;
		System.out.println(dd);
		
		char c='@';
		int ii=10;
		int e=ii+c;
		System.out.println(e);
		
		int i=60;
		float ff=78.96f;
		float fff=i;
		float p=fff+ff;
		System.out.println(p);
		
		

	}

}
